#!/bin/bash
project=`gcloud config list | grep ^project | awk '{print $3}'`
zone=`gcloud config list | grep ^zone | awk '{print $3}'`
gcloud beta container --project ${project} clusters create "pwx-cluster-1" --zone ${zone} --username "admin" --cluster-version "1.7.11-gke.1" --machine-type "n1-standard-1" --image-type "UBUNTU" --disk-size "10" --scopes "https://www.googleapis.com/auth/compute","https://www.googleapis.com/auth/devstorage.read_only","https://www.googleapis.com/auth/logging.write","https://www.googleapis.com/auth/monitoring","https://www.googleapis.com/auth/servicecontrol","https://www.googleapis.com/auth/service.management.readonly","https://www.googleapis.com/auth/trace.append" --num-nodes "3" --network "default" --enable-cloud-logging --enable-cloud-monitoring --subnetwork "default" --enable-legacy-authorization
